{
  "name": "Transcode Videos to MP4/WebM",
  "description": "Creates web-friendly versions upon video upload.",
  "trigger": {
    "event": "upload.completed",
    "conditions": [
      {
        "prop": "file.type",
        "value": "video/*"
      }
    ]
  },
  "workflow": [
    {
      "kind": "video",
      "format": "mp4",
      "resize": "fit",
      "width": 1920,
      "height": 1080,
      "label": "web_1080p_mp4"
    },
    {
      "kind": "video",
      "format": "webm",
      "resize": "fit",
      "width": 1920,
      "height": 1080,
      "label": "web_1080p_webm"
    }
  ]
}

